function hide(e) {
    document.getElementById(e).style.display = "none"
}
function show(e) {
    document.getElementById(e).style.display = "inline-flex"
}
function show2(e) {
    document.getElementById(e).style.display = "inline"
}
function about(e) {
    document.getElementById(e).style.display = "flex",
        document.getElementById(e).style.position = "fixed"
}
function abouthide(e) {
    document.getElementById(e).style.display = "none"
}
function startcolor(e) {
    document.body.style.background = "linear-gradient(45deg, #130c31, #40284c)"
}
function feedcolor(e) {
    document.body.style.background = "linear-gradient(45deg, #f2c3aa, #30fbfd)"
}

